import { useRef, useState } from "react";

import styles from "./Modal.module.css";

export default function Modal({ setAddModal, fetchPosts }) {
  const tagInput = useRef(null);

  const initialErrors = {
    title: '',
    link: '',
    description: '',
    tags: []
  };

  const [errors, setErrors] = useState(initialErrors);

  const [title, setTitle] = useState("");
  const [link, setLink] = useState("");
  const [description, setDescription] = useState("");
  const [tag, setTag] = useState("");
  const [tags, setTags] = useState([]);


  function validateForm() {
    if(title.length === 0) {
      setErrors({...errors, title: "Title is required"})
      console.log("title", errors)
    }
    // if(errors.link.length === 0) {
    //   setErrors({...errors, link: "Title is required"})
    //   console.log("link", errors)
    // }
    // if(errors.description.length === 0) {
    //   setErrors({...errors, description: "Title is required"})
    //   console.log("description", errors)
    // }
    // if(errors.tags.length === 0) {
    //   setErrors({...errors, tags: "Title is required"})
    //   console.log("tags", errors)
    // }
  }

  async function handleSubmit(e) {
    e.preventDefault();
    setErrors(initialErrors);

    validateForm();

    if(errors.length === 0) {
      await fetch("http://localhost:3000/tools", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          title,
          link,
          description,
          tags,
        }),
      });
      fetchPosts();
      handleCloseModal();   
    }  
  }

  function handleCloseModal() {
    setAddModal(false);
    setTitle("");
    setDescription("");
    setLink("");
    setTag("");
    setTags([]);
  }

  function handleAddTag() {
    const trimTag = tag.trim();
    if (trimTag) {
      handleFocus();
      setTags([...tags, trimTag]);
      setTag("");
    }
    return handleFocus();
  }

  function handleRemoveTag(e) {
    const result = tags.filter((word) => word != e);
    setTags(result);
  }

  function handleFocus() {
    tagInput.current.focus();
  }

  return (
    <div className={styles.modal}>
      <form
        onSubmit={handleSubmit}
        onKeyPress={(e) => {
          e.key === "Enter" && e.preventDefault();
        }}
      >
        <div className={styles.modalBody}>
          <div className={styles.modalHeader}>
            <div className={styles.modalHeaderAdd}>
              <svg
                xmlns="http://www.w3.org/2000/svg"
                width="61.414"
                height="61.42"
                viewBox="0 0 61.414 61.42"
                style={{
                  fill: "none",
                  stroke: "#170C3A",
                  strokeMiterlimit: 10,
                  strokeWidth: "10px",
                  transform: "rotate(45deg)",
                  width: "15px",
                  height: "15px",
                }}
              >
                <defs></defs>
                <g transform="translate(-568.793 -714.793)">
                  <path
                    className="a"
                    d="M80,20.005l-60,60m60,0L20,20"
                    transform="translate(549.501 695.5)"
                  />
                </g>
              </svg>
              <h3 className={styles.modalTitle}>Add a new tool</h3>
            </div>
            <svg
              className={styles.closeModal}
              onClick={handleCloseModal}
              xmlns="http://www.w3.org/2000/svg"
              width="13.414"
              height="13.415"
              viewBox="0 0 13.414 13.415"
              style={{
                fill: "none",
                stroke: "#8f8a9b",
                strokeMiterlimit: 10,
                strokeWidth: "2px",
              }}
            >
              <defs></defs>
              <path
                className="a"
                d="M32,20,20,32m12,0L20,20"
                transform="translate(-19.292 -19.293)"
              />
            </svg>
          </div>
          <div className={styles.modalContent}>
            <label className={styles.modalLabel}>Tool Title</label>
            <input
              className={styles.modalInput}
              type="text"
              value={title}
              onChange={(e) => {
                setTitle(e.target.value);
              }}
            />

            { errors.title.length > 0 && <p>The title is required</p>}

            <label className={styles.modalLabel}>Link</label>
            <input
              className={styles.modalInput}
              type="text"
              value={link}
              onChange={(e) => {
                setLink(e.target.value);
              }}
            />

            <label className={styles.modalLabel}>Description</label>
            <textarea
              className={styles.modalInputTextArea}
              type="text"
              value={description}
              onChange={(e) => {
                setDescription(e.target.value);
              }}
            />

            <label className={styles.modalLabel}>Tags</label>
            <div className={styles.addTags}>
              <input
                ref={tagInput}
                className={styles.modalInput}
                type="text"
                value={tag}
                onKeyPress={(e) => {
                  e.key === "Enter" && (e.preventDefault(), handleAddTag());
                }}
                onChange={(e) => {
                  setTag(e.target.value);
                }}
              />
              <div
                onClick={() => handleAddTag()}
                className={styles.addTagField}
              >
                <svg
                  className={styles.addTag}
                  xmlns="http://www.w3.org/2000/svg"
                  viewBox="0 0 61.414 61.42"
                >
                  <g transform="translate(-568.793 -714.793)">
                    <path
                      className="addTag"
                      d="M80,20.005l-60,60m60,0L20,20"
                      transform="translate(549.501 695.5)"
                    />
                  </g>
                </svg>
              </div>

              <p>
                <div className={styles.tagsContainer}>
                  {tags &&
                    tags.map((tag) => {
                      return (
                        <div
                          className={styles.tag}
                          value={tag}
                          onClick={() => handleRemoveTag(tag)}
                        >
                          <p>{tag}</p>
                          <svg
                            className={styles.removeTag}
                            xmlns="http://www.w3.org/2000/svg"
                            width="10"
                            height="10"
                            viewBox="0 0 13.414 13.415"
                            style={{
                              fill: "none",
                              stroke: "#8f8a9b",
                              strokeMiterlimit: 10,
                              strokeWidth: "2px",
                            }}
                          >
                            <defs></defs>
                            <path
                              className="a"
                              d="M32,20,20,32m12,0L20,20"
                              transform="translate(-19.292 -19.293)"
                            />
                          </svg>
                        </div>
                      );
                    })}
                </div>
              </p>
            </div>
          </div>

          <footer className={styles.modalFooter}>
            <button className={styles.cancelButton} onClick={handleCloseModal}>
              Cancel
            </button>
            <button className={styles.saveButton} type="submit" value="Submit">
              Save
            </button>
          </footer>
        </div>
      </form>
    </div>
  );
}
