import Head from "next/head";
import Image from "next/image";
import { useEffect, useState } from "react";

import styles from "../styles/Home.module.css";

import Card from "../components/Card";
import Modal from "../components/Modal";
import ConfirmationModal from "../components/ConfirmationModal";


export default function Home() {
  const [posts, setPosts] = useState([]);
  const [searchInput, setSearchInput] = useState("");
  const [searchForTags, setSearchForTags] = useState(false);

  const [toolSelected, setToolSelected] = useState({});

  const [addModal, setAddModal] = useState(false);
  const [confirmationModal, setConfirmationModal] = useState(false);

  async function fetchPosts() {
    const res = await fetch("http://localhost:3000/tools");
    const data = await res.json();
    setPosts(data);
  }

  async function fetchFilteredPosts() {
    if (!searchForTags) {
      const res = await fetch(`http://localhost:3000/tools?q=${searchInput}`);
      const data = await res.json();
      setPosts(data);
    } else {
      const res = await fetch(
        `http://localhost:3000/tools?tags_like=${searchInput}`
      );
      const data = await res.json();
      setPosts(data);
    }
  }

  useEffect(() => {
    const timeoutId = setTimeout(() => fetchFilteredPosts(), 500);
    return () => clearTimeout(timeoutId);
  }, [searchInput, searchForTags]);

  useEffect(() => {
    fetchPosts();
  }, []);

  return (
    <div className={styles.container}>
      <Head>
        <title>VUTTR</title>
        <link rel="icon" href="/favicon.ico" />
      </Head>

      <main className={`${styles.main} ${addModal && styles.modalIsOpen}`}>
        <h1 className={styles.title}>VUTTR</h1>
        <h2>Very Useful Tools to Remember</h2>

        <div className={styles.header}>
          <div className={styles.headerLeft}>
            <svg
              className={styles.searchIcon}
              xmlns="http://www.w3.org/2000/svg"
              width="47.707"
              height="47.707"
              viewBox="0 0 47.707 47.707"
              style={{
                fill: "none",
                stroke: "#170c3a",
                strokeMiterlimit: 10,
                strokeWidth: "2px",
                width: "20px",
                height: "20px",
              }}
            >
              <defs></defs>
              <g transform="translate(-1272 -1799)">
                <path
                  className="a"
                  d="M39.049,39.049,56,56"
                  transform="translate(1263 1790)"
                />
                <circle
                  className="a"
                  cx="17"
                  cy="17"
                  r="17"
                  transform="translate(1273 1800)"
                />
              </g>
            </svg>
            <input
              className={styles.searchInput}
              type="text"
              placeholder="search"
              value={searchInput}
              onChange={(e) => setSearchInput(e.target.value)}
            ></input>
            <input
              type="checkbox"
              value={searchForTags}
              onChange={() => setSearchForTags(!searchForTags)}
            />
            <label className={styles.searchLabel}>search in tags only</label>
          </div>

          <button
            className={styles.addButton}
            onClick={() => setAddModal(true)}
          >
            <svg
              xmlns="http://www.w3.org/2000/svg"
              width="61.414"
              height="61.42"
              viewBox="0 0 61.414 61.42"
              style={{
                fill: "none",
                stroke: "#FFFFFF",
                strokeMiterlimit: 10,
                strokeWidth: "10px",
                transform: "rotate(45deg)",
                width: "15px",
                height: "15px",
              }}
            >
              <defs></defs>
              <g transform="translate(-568.793 -714.793)">
                <path
                  className="a"
                  d="M80,20.005l-60,60m60,0L20,20"
                  transform="translate(549.501 695.5)"
                />
              </g>
            </svg>
            <label className={styles.addLabel}>Add</label>
          </button>
        </div>

        {!posts.length == 0 ? (
          posts.map((post) => {
            return <Card key={post.id} content={post} onDelete={fetchPosts} />;
          })
        ) : (
          <div className={styles.noPosts}>
            <div className={styles.noPostsImg}>
              <Image
                src="/caveman.gif"
                alt="Not Found"
                width={500}
                height={400}
              />
              <h2 className={styles.notFound}>Ooops, no posts found...</h2>
            </div>
          </div>
        )}

        {addModal && (
          <Modal setAddModal={setAddModal} fetchPosts={fetchPosts} />
        )}

        {/* <ConfirmationModal tool={selectedTool}/> */}

      </main>
    </div>
  );
}
