import styles from "../Modal/Modal.module.css";

export default function ConfirmationModal({ openConfirmationModal, tool }) {
   
  async function handleSubmit(e) {
    e.preventDefault();
    await validateForm();
  }

  function handleCloseModal() {
    openConfirmationModal(false);
  }

  return (
    <div className={styles.modal}>
      <form
        onSubmit={handleSubmit}
        onKeyPress={(e) => {
          e.key === "Enter" && e.preventDefault();
        }}
      >
        <div className={styles.modalBody}>
          <div className={styles.modalHeader}>
            <div className={styles.modalHeaderAdd}>
              <svg
                xmlns="http://www.w3.org/2000/svg"
                width="61.414"
                height="61.42"
                viewBox="0 0 61.414 61.42"
                style={{
                  fill: "none",
                  stroke: "#170C3A",
                  strokeMiterlimit: 10,
                  strokeWidth: "10px",
                  transform: "rotate(45deg)",
                  width: "15px",
                  height: "15px",
                }}
              >
                <defs></defs>
                <g transform="translate(-568.793 -714.793)">
                  <path
                    className="a"
                    d="M80,20.005l-60,60m60,0L20,20"
                    transform="translate(549.501 695.5)"
                  />
                </g>
              </svg>
              <h3 className={styles.modalTitle}>Are you sure?</h3>
            </div>
            <svg
              className={styles.closeModal}
              onClick={handleCloseModal}
              xmlns="http://www.w3.org/2000/svg"
              width="13.414"
              height="13.415"
              viewBox="0 0 13.414 13.415"
              style={{
                fill: "none",
                stroke: "#8f8a9b",
                strokeMiterlimit: 10,
                strokeWidth: "2px",
              }}
            >
              <defs></defs>
              <path
                className="a"
                d="M32,20,20,32m12,0L20,20"
                transform="translate(-19.292 -19.293)"
              />
            </svg>
          </div>

            <p>{`Are you sure to remove the tool <strong>"${tool.title}"<strong> from the list?`}</p>

          <footer className={styles.modalFooter}>
            <button className={styles.cancelButton} onClick={handleCloseModal}>
              Cancel
            </button>
            <button className={styles.saveButton} type="submit" value="Submit">
              Confirm
            </button>
          </footer>
        </div>
      </form>
    </div>
  );
}
